const express = require('express');
const app = express();
const port = 3000;

// กำหนดเส้นทางหลัก
app.get('/', (req, res) => {
  res.send('Hello, World!');
});

// กำหนดเส้นทางใหม่ /about
app.get('/about', (req, res) => {
  res.send('This is the about page');
});

// รันเซิร์ฟเวอร์
app.listen(port, () => {
  console.log(`Server is running on http://localhost:${port}`);
});